mapper.select:
select 

from config
where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}

mapper.delete:
delete from config
where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}

mapper.select:
select count(*) 
from config
where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}
