mapper.select:
select 

from log
where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}
  and error_type = #{errorType,jdbcType=VARCHAR}
  and describe_message = #{describeMessage,jdbcType=VARCHAR}

mapper.delete:
delete from log
where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}
  and error_type = #{errorType,jdbcType=VARCHAR}
  and describe_message = #{describeMessage,jdbcType=VARCHAR}

mapper.update:
update log

  
    read_status = #{readStatus,jdbcType=VARCHAR},
  
  
    send_status = #{sendStatus,jdbcType=VARCHAR},
  

where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}
  and error_type = #{errorType,jdbcType=VARCHAR}
  and describe_message = #{describeMessage,jdbcType=VARCHAR}

update log
set read_status = #{readStatus,jdbcType=VARCHAR},
  send_status = #{sendStatus,jdbcType=VARCHAR}
where ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and port = #{port,jdbcType=INTEGER}
  and time = #{time,jdbcType=TIMESTAMP}
  and error_type = #{errorType,jdbcType=VARCHAR}
  and describe_message = #{describeMessage,jdbcType=VARCHAR}
