mapper.select:
select 

from memory
where Time = #{time,jdbcType=TIMESTAMP}
  and Port = #{port,jdbcType=INTEGER}
  and ipAddress = #{ipaddress,jdbcType=VARCHAR}

mapper.delete:
delete from memory
where Time = #{time,jdbcType=TIMESTAMP}
  and Port = #{port,jdbcType=INTEGER}
  and ipAddress = #{ipaddress,jdbcType=VARCHAR}

mapper.update:
update memory

  
    Free = #{free,jdbcType=DOUBLE},
  
  
    Total = #{total,jdbcType=DOUBLE},
  
  
    Max = #{max,jdbcType=DOUBLE},
  

where Time = #{time,jdbcType=TIMESTAMP}
  and Port = #{port,jdbcType=INTEGER}
  and ipAddress = #{ipaddress,jdbcType=VARCHAR}

update memory
set Free = #{free,jdbcType=DOUBLE},
  Total = #{total,jdbcType=DOUBLE},
  Max = #{max,jdbcType=DOUBLE}
where Time = #{time,jdbcType=TIMESTAMP}
  and Port = #{port,jdbcType=INTEGER}
  and ipAddress = #{ipaddress,jdbcType=VARCHAR}

mapper.select:
select 

from memory  
where
ipAddress = #{ipaddress}    
and port=#{port} 
order by time DESC limit 1
