mapper.select:
select 

from memory_pool
where Name = #{name,jdbcType=VARCHAR}
  and Port = #{port,jdbcType=INTEGER}
  and ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and Time = #{time,jdbcType=TIMESTAMP}

mapper.delete:
delete from memory_pool
where Name = #{name,jdbcType=VARCHAR}
  and Port = #{port,jdbcType=INTEGER}
  and ipAddress = #{ipaddress,jdbcType=VARCHAR}
  and Time = #{time,jdbcType=TIMESTAMP}

mapper.insert:
