  Time, Type, Initial, 
  Committed, Maximum, Used
  )
values (#{name,jdbcType=VARCHAR}, #{port,jdbcType=INTEGER}, #{ipaddress,jdbcType=VARCHAR}, 
  #{time,jdbcType=TIMESTAMP}, #{type,jdbcType=VARCHAR}, #{initial,jdbcType=DOUBLE}, 
  #{committed,jdbcType=DOUBLE}, #{maximum,jdbcType=DOUBLE}, #{used,jdbcType=DOUBLE}
  )

mapper.update:
update memory_pool

  
    Type = #{type,jdbcType=VARCHAR},
  
  
